import styled from "styled-components";

export const Container = styled.div`
width: 100%;
height: 100vh;
background-color: #CACACA;

display: flex;
align-items: center;
justify-content: center;

`

export const Content = styled.div`
background-color: #FFFFFF;
width: 50%
min-height: 350px;
`
