import styled from "styled-components";

export const InputContainer = styled.div`
width: 100%;
height: 75 px;
background-color: #AAFFAA;

display: flex;
align-items: center;
justify-content: flex-end;

font-size: 24px;
font-family: 'Roboto';

input {
    width: 100%;
    height: auto;
    background-color: #AAFFAA;
    border: 0;
    padding: 0 10px;
    align: right;
    font-size: 24px;
    font-family: 'Roboto';
    color: #FFFFFF;

}
`
